mapper.delete:

delete from ecs_link_goods
where goods_id = #{goodsId,jdbcType=INTEGER}
  and link_goods_id = #{linkGoodsId,jdbcType=INTEGER}
  and admin_id = #{adminId,jdbcType=TINYINT}

mapper.update:

update ecs_link_goods
set is_double = #{isDouble,jdbcType=BIT}
where goods_id = #{goodsId,jdbcType=INTEGER}
  and link_goods_id = #{linkGoodsId,jdbcType=INTEGER}
  and admin_id = #{adminId,jdbcType=TINYINT}

mapper.select:

select goods_id, link_goods_id, admin_id, is_double
from ecs_link_goods
where goods_id = #{goodsId,jdbcType=INTEGER}
  and link_goods_id = #{linkGoodsId,jdbcType=INTEGER}
  and admin_id = #{adminId,jdbcType=TINYINT}
