mapper.delete:

delete from ecs_package_goods
where package_id = #{packageId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}
  and admin_id = #{adminId,jdbcType=TINYINT}
  and product_id = #{productId,jdbcType=INTEGER}

mapper.update:

update ecs_package_goods
set goods_number = #{goodsNumber,jdbcType=SMALLINT}
where package_id = #{packageId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}
  and admin_id = #{adminId,jdbcType=TINYINT}
  and product_id = #{productId,jdbcType=INTEGER}

mapper.select:

select package_id, goods_id, admin_id, product_id, goods_number
from ecs_package_goods
where package_id = #{packageId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}
  and admin_id = #{adminId,jdbcType=TINYINT}
  and product_id = #{productId,jdbcType=INTEGER}
