mapper.delete:

delete from ecs_supplier_goods_cat
where goods_id = #{goodsId,jdbcType=INTEGER}
  and cat_id = #{catId,jdbcType=SMALLINT}

mapper.insert:

insert into ecs_supplier_goods_cat (goods_id, cat_id, supplier_id
  )
values (#{goodsId,jdbcType=INTEGER}, #{catId,jdbcType=SMALLINT}, #{supplierId,jdbcType=INTEGER}
  )

mapper.update:

update ecs_supplier_goods_cat
set supplier_id = #{supplierId,jdbcType=INTEGER}
where goods_id = #{goodsId,jdbcType=INTEGER}
  and cat_id = #{catId,jdbcType=SMALLINT}

mapper.select:

select goods_id, cat_id, supplier_id
from ecs_supplier_goods_cat
where goods_id = #{goodsId,jdbcType=INTEGER}
  and cat_id = #{catId,jdbcType=SMALLINT}
